<template>  <div class="AddMethod paddingtop-50">    <NavTit :title="title" :noTit="false"></NavTit>    <ul class="methodList padding-20 paddingtop-50">      <li v-for="(item, index) in list" class="flex" @click="toAdd(item.name)">        <img :src="item.icon" alt="" />        <span class="fontweight-m fontsize20">{{ item.name }}</span>      </li>    </ul>  </div></template><script>import NavTit from '../NavAndTit.vue';export default {  name: 'AddMethod',  components: {    NavTit  },  data() {    return {      title: 'AddMethod',      list: [{ name: 'IMPS', icon: require('@/assets/img/UPI_icon@2x.png') }, { name: 'UPI', icon: require('@/assets/img/UPI@2x.png') }]    };  },  created() {},  mounted() {},  methods: {    toAdd(name) {      this.$router.push({        path: '/add',        query: {
          name: name
        }      });    }  }};</script><style lang="scss" scoped>.methodList li {  padding: 25px 20px;  background-color: #f7f8fc;  margin-bottom: 20px;  line-height: 40px;  position: relative;  &::before {    position: absolute;    content: '';    background: url(../../assets/img/arrow_right@2x.png) no-repeat center;    background-size: cover;    width: 24px;    height: 24px;    right: 20px;    top: 50%;    transform: translateY(-50%);  }  img {    height: 40px;    width: 40px;    margin-right: 20px;  }}</style>
