<template>  <div class="Answer paddingtop-50">    <NavTit :noTit="true"></NavTit>    <h4 v-if="details.number" class="fontweight-m fontsize20 padding-20" style="margin-bottom: 20px;">{{ 'Q' + details.number + ': ' + details.question }}</h4>    <div v-if="details.answer" class="padding-20 lineheight-20" style="line-height: 22px;">{{ details.answer }}</div>  </div></template><script>import NavTit from '../NavAndTit.vue';import { userQaDetail } from '@/api/api';export default {  name: 'Answer',  components: {    NavTit  },  data() {    return {      details: {}    };  },  created() {    userQaDetail({      id: this.$route.params.id    })      .then(res => {        if (res.code == 200) {          console.log(res.result);          this.details = res.result;        }      })      .catch(err => {        console.log(err);      });  },  mounted() {},  methods: {}};</script><style lang="scss" scoped></style>
