<template>  <div class="helpAndSupport paddingtop-50">    <div class="padding-20"><NavTit :title="title" :noTit="false" :isWhite="false" :kefu="true"></NavTit></div>    <section class="main">      <ul>        <li @click="toAnswer(item.id)" class="fontsize16 thinBorder" v-for="(item, index) in list" :key="index">{{ item.number + '.' + item.question }}</li>      </ul>    </section>  </div></template><script>import { userQaList } from '@/api/api';import NavTit from '../NavAndTit.vue';export default {  name: 'HelpAndSupport',  components: {    NavTit  },  data() {    return {      title: 'Help & Support',      list: []    };  },  created() {    userQaList()      .then(res => {        if (res.code == 200) {          this.list = res.result;        }      })      .catch(err => {        console.log(err);      });  },  mounted() {},  methods: {    onClickLeft() {      this.$router.go(-1);    },    toAnswer(id) {      this.$router.push('/answer/' + id);    }  }};</script><style lang="scss" scoped>.helpAndSupport {  background-color: #f5f6fa;  .main {    margin-top: 30px;    li {      padding: 17px 0;      line-height: 24px;    }  }}</style>
