<template>  <div class="uploadVoucher paddingtop-50 padding-20">    <NavTit :title="title" :noTit="false"></NavTit>    <p class="desc fontsize12 block">{{ desc }}</p>    <div class="confirmContent">      <p class="flex">        <b>          AMOUNT          <em class="inlineblock">(USDT)</em>        </b>        <span class="fontweight-m">{{ orderDetail.amountTransfer }}</span>      </p>      <p class="flex">        <b>          AT PRICE          <em class="inlineblock">(Per USDT)</em>        </b>        <span class="fontweight-m">₹{{ orderDetail.price }}</span>      </p>      <div class="fontweight-m flex">        <b>TOTAL</b>        <span>          ₹          <i class="fontsize20 fontweight-m">            {{ orderDetail.totalINR }}            <!-- {{              noFee                ? (perUSDT * parseFloat(volumeUSDT.replace('₹', '')) - perUSDT * parseFloat(volumeUSDT.replace('₹', '')) * fee).toFixed(2)                : (perUSDT * parseFloat(volumeUSDT.replace('₹', ''))).toFixed(2)            }} -->          </i>        </span>      </div>    </div>    <div>      <van-form @submit="onSubmit">        <van-field          v-model="form.from"          name="from"          label="From"          placeholder="Please Enter Your Payment Address"          :rules="[{ required: true, message: 'Please Enter Your Payment Address' }]"        />        <van-field @click="toSelectAdress" v-model="form.to" name="to" label="To" readonly :rules="[{ required: true }]" />        <van-field          v-model="form.txid"          name="txid"          label="TxID"          readonly          placeholder="Please Enter transaction ID"          :rules="[{ required: true, message: 'Please Enter transaction ID' }]"        />        <van-field @click="toSelectPayment" v-model="form.paymentMethod" name="paymentMethod" label="Payment Method" :rules="[{ required: true }]" />        <div v-if="paymentMethod" class="bankCard white-color">          <p class="p1 flex">            <img class="icon" :src="getIcon(paymentMethod.payType)" alt="" />            <span class="fontweight-m fontsize12">{{ paymentMethod.account }}</span>          </p>          <p class="p2 fontsize12 flex">{{ paymentMethod.name }}</p>          <p class="p3 fontweight-m flex ">            <span v-if="paymentMethod.payType == 'IMPS'" class="fontweight-m fontsize16">{{ paymentMethod.isfcCode }}</span>            <img v-else-if="paymentMethod.payType == 'UPI'" class="qrCode" :src="paymentMethod.qrCode" alt="" />          </p>        </div>        <van-field v-model="form.transferVoucher" name="transferVoucher" label="Transfer Voucher" :rules="[{ required: true, message: 'Please Upload Your Transfer Voucher' }]" />        <van-uploader v-model="fileList" multiple :max-count="1" :after-read="afterRead" />        <div class="bottomSubmit text-center">          <p class="fontsize12">            COUNTDOWN:            <van-count-down class="inlineblock fontweight-m color-6d4ffd" :time="time" millisecond format="HH:mm:ss:SS" />            <!-- <span class="fontweight-m color-6d4ffd">120min</span> -->          </p>          <van-button type="primary" block native-type="submit">Comfirm and Submit</van-button>        </div>      </van-form>    </div>  </div></template><script>import NavTit from '../NavAndTit.vue';import { orderDetail } from '@/api/api';export default {  name: 'UploadVoucher',  components: {    NavTit  },  data() {    return {      title: 'Upload Transfer Voucher',      desc: 'You’ll have to upload the transfer voucher',      id: '',      fileList: [],      paymentMethod: {},      orderDetail: {},      time: 7200000,      form: {        from: '',        to: '',        txid: '',        paymentMethod: '',        transferVoucher: ''      }    };  },  created() {    this.id = this.$route.query.id;    orderDetail({      goodsId: this.id    })      .then(res => {        if (res.code == 200) {          this.orderDetail = res.result;          console.log(this.orderDetail);          this.orderDetail.sourceAddress = '3435345';          this.orderDetail.transactionNumber = '3432547687wksd5345';          this.form.from = this.orderDetail.sourceAddress;          this.form.txid = this.orderDetail.transactionNumber;          this.paymentMethod = this.orderDetail.paymentVo;          this.form.paymentMethod = this.paymentMethod.account;          if (this.$store.state.address) {            this.form.to = this.$store.state.address;            this.$store.commit('coverAddress', '');          } else {            this.form.to = this.orderDetail.paymentadress;          }        }      })      .catch(err => {        console.log(err);      });  },  mounted() {    // console.log(this.amount, this.price, this.total);  },  methods: {    onSubmit() {      console.log(this.form);    },    afterRead(file) {      this.form.transferVoucher = file.content;    },    toSelectAdress() {      this.$router.push({        path: '/selladdress',        query: {          amount: this.orderDetail.amountTransfer        }      });    },    toSelectPayment() {      this.$router.push({        path: '/payment',        query: {          from: 'UploadVoucher'        }      });    }  }};</script><style lang="scss" scoped>.uploadVoucher {  padding-bottom: 100px;  .desc {    color: #8c9fad;    margin-top: 4px;    background: url(../../assets/img/tips_icon@2x.png) no-repeat left center;    background-size: 12px;    text-indent: 18px;  }}.confirmContent {  margin: 20px 0 0;  padding: 22px 20px 18px;  line-height: 1;  p {    &:nth-child(2) {      margin: 24px 0 22px;    }  }  div {    padding-top: 15px;  }}/deep/ .van-cell {  input {    &[name='transferVoucher'] {      height: 0;    }  }  &:nth-child(2),  &:nth-child(4) {    position: relative;    background: url(../../assets/img/arrow@2x.png) no-repeat right 38px;    background-size: 24px;  }  &:nth-child(4) {    margin-bottom: 10px;  }  &:nth-child(6) {    &::after {      border: none;    }  }}/deep/ .van-button--primary {  height: 50px;  &.van-button--disabled {    background-color: #e7ebee;    opacity: 1;    color: #bac7cf;  }}.bankCard {  width: 335px;  height: 92px;  padding: 10px 20px;  box-sizing: border-box;  background: url(../../assets/img/card_bg@2x.png) no-repeat center top;  background-size: 100%;  .p1 {    line-height: 18px;    margin-bottom: 15px;  }  .p2 {    margin-bottom: 2px;  }  .p3 {    line-height: 30px;  }  .icon {    height: 18px;    width: 18px;    margin-right: 10px;  }  .qrCode {    width: 30px;    height: 30px;  }}</style>
